import { ADD_FAV, REMOVE_FAV, SORT } from "./actions/actions-types"

const initialState = {
    myFavorites:[]
}

const reducer = (state = initialState, action)=> {
    switch(action.type){
        case ADD_FAV:
            return {...state,
            myFavorites:[...state.myFavorites, action.payload]}
        
        case REMOVE_FAV:
            return {...state,
            myFavorites: state.myFavorites
            .filter(character=> character.id !== action.payload)}
        
        case SORT:
            const sorted = state.myFavorites.sort((a,b)=>{
                if(action.payload==="Ascendent") return a.id - b.id 
                if(action.payload==="Descendent") return b.id - a.id 
            })
            
            return {...state, myFavorites:[...sorted]}
        
        default:
            return {...state}
    }
}

export default reducer
