import { connect, useDispatch } from "react-redux"
import Card from "../Card"
import { filter, removeFav, sort } from "../../redux/actions/actions"

const Favorites = ({myFavorites, onClose, removeFav, sort, filter})=>{
    const dispatch = useDispatch()
    
    const closeFavorite = (id)=>{
        onClose(id)
        removeFav(id)
    }

    const handleSort = (event) => {
        dispatch(sort(event.target.value))
    }

    const handleFilter = (event) => {
        dispatch(filter(event.target.value))
    }

    return(
        <div>
      <div>
         <select name="sort" defaultValue="Default" onChange={handleSort}>
            <option value="Default" disabled>Default</option>
            <option value="Ascendent">Ascendent</option>
            <option value="Descendent">Descendent</option>
         </select>
         <select name="filter" defaultValue="Default" onChange={handleFilter}>
            <option value="Default" disabled>Default</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Genderless">Genderless</option>
            <option value="unknown">unknown</option>
         </select>
      </div>
      <div>
            {myFavorites && myFavorites.map(favChar => <Card
            id={favChar.id}
            name={favChar.name}
            status={favChar.status}
            species={favChar.species}
            gender={favChar.gender}
            origin={favChar.origin.name}
            image={favChar.image}
            onClose={()=>closeFavorite(favChar.id)}
         />)}
        </div>
   </div>
    )
}

export const mapStateToProps = (state) => {
    return {
        myFavorites: state.myFavorites
    }
}

export const mapDispatchToProps = (dispatch) => {
    return {
        removeFav: id=>dispatch(removeFav(id))
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Favorites)
